mapper.select:
       	select *, date_format(regDate, '%Y년 %m월%d일 %T')as fmtDate 
		from view_bbs

			where uid like #{word}


			where uname like #{word}


			where title like #{word}


			where contents like #{word}

		order by bid desc 
		limit #{start}, #{size}

   		select count(*) total
   		from view_bbs

			where uid like #{word}


			where uname like #{word}


			where title like #{word}


			where contents like #{word}

mapper.update:
update bbs
set viewCnt=viewCnt+1
where bid=#{bid}
